mapper.select:
SELECT 
FROM 
WHERE d.dataset_key=#{key.datasetKey} AND d.id = #{key.id}

mapper.insert:
INSERT INTO distribution (dataset_key,taxon_id,id,, created, modified) SELECT #{newDatasetKey},coalesce(m.id2,taxon_id),nextval('distribution_${newDatasetKey}_id_seq'),, created, modified FROM distribution d
LEFT JOIN idmap_name_usage_${datasetKey} m ON m.id=d.taxon_id
WHERE dataset_key = #{datasetKey}

mapper.select:
SELECT EXISTS (
  SELECT TRUE FROM distribution WHERE dataset_key=#{datasetKey}
