mapper.select:
SELECT 
FROM 
WHERE m.dataset_key=#{key.datasetKey} AND m.id = #{key.id}

mapper.insert:
INSERT INTO media (dataset_key, taxon_id, id, , created, modified) SELECT #{newDatasetKey},coalesce(m.id2,taxon_id),nextval('media_${newDatasetKey}_id_seq'),, created, modified FROM media md
LEFT JOIN idmap_name_usage_${datasetKey} m ON m.id=md.taxon_id
WHERE dataset_key = #{datasetKey}

