mapper.sql:
v.issues,
s.subject_dataset_key AS sector_dataset_key,
s.mode AS n_sector_mode,




FROM name n
LEFT JOIN sector s ON s.dataset_key=n.dataset_key AND s.id=n.sector_key
LEFT JOIN name_usage u ON n.id = u.name_id AND u.dataset_key=#{datasetKey}
LEFT JOIN verbatim v ON v.id= n.verbatim_key AND v.dataset_key=#{datasetKey}
WHERE n.dataset_key=#{datasetKey}
  AND u.id IS NULL

mapper.select:
WITH RECURSIVE tree AS (
  SELECT #{usageId} AS uid, classification_sn(#{datasetKey}, #{usageId}, true) AS classification
UNION ALL
  SELECT u.id, array_append(tree.classification, (u.id,n.rank,n.scientific_name,n.authorship)::simple_name)
  FROM name_usage u
    JOIN name n ON u.name_id=n.id AND n.dataset_key=#{datasetKey}
    JOIN tree ON (tree.uid = u.parent_id)
  WHERE u.dataset_key=#{datasetKey}

    AND u.sector_key = #{sectorKey}

)
SELECT * FROM tree

  WHERE uid != #{usageId} OR (SELECT sector_key=#{sectorKey} FROM name_usage WHERE dataset_key=#{datasetKey} AND id=#{usageId})

WITH nu AS ( SELECT u.id, u.name_id, u.verbatim_key AS uvkey, n.verbatim_key AS nvkey, u.verbatim_source_key AS uvskey, n.verbatim_source_key AS nvskey FROM name_usage u JOIN name n ON u.name_id=n.id AND n.dataset_key=u.dataset_key WHERE u.dataset_key=#{datasetKey} AND u.id = #{id} ), nuissues AS ( SELECT iss.id, array_cat_agg(iss.issues) AS issues FROM (
) iss GROUP BY iss.id ), usrc AS ( SELECT v.verbatim_source_key, array_agg(DISTINCT v.type) AS groups, array_agg(DISTINCT v.source_dataset_key) AS sources FROM nu JOIN verbatim_source_secondary v ON nu.uvskey=v.verbatim_source_key WHERE v.dataset_key=#{datasetKey} GROUP BY v.id ), nsrc AS ( SELECT v.verbatim_source_key, array_agg(DISTINCT v.type) AS groups, array_agg(DISTINCT v.source_dataset_key) AS sources FROM nu JOIN verbatim_source_secondary v ON nu.nvskey=v.verbatim_source_key WHERE v.dataset_key=#{datasetKey} GROUP BY v.id )
SELECT u.id AS uid, , ed.id AS ed_id, ed.dataset_key AS ed_dataset_key, ed.mode AS ed_mode, v.issues, src.sources, src.groups FROM LEFT JOIN nuissues v ON v.id=u.verbatim_source_key LEFT JOIN usrc ON usrc.verbatim_source_key=u.verbatim_source_key LEFT JOIN nsrc ON nsrc.verbatim_source_key=u.verbatim_source_key LEFT JOIN decision ed ON ed.subject_dataset_key=#{datasetKey} AND ed.subject_id=u.id WHERE u.dataset_key=#{datasetKey} AND u.id = #{id}

SELECT u.id AS uid, NULL AS classification, , r.citation AS according_to, v.issues, v2.sources, v2.groups, ed.id AS ed_id, ed.dataset_key AS ed_dataset_key, ed.mode AS ed_mode FROM LEFT JOIN tmp_usage_issues v ON v.id=u.verbatim_source_key LEFT JOIN tmp_usage_sources v2 ON v2.id=u.id LEFT JOIN decision ed ON ed.subject_dataset_key=#{datasetKey} AND ed.subject_id=u.id WHERE u.dataset_key=#{datasetKey}
AND u.sector_key = #{sectorKey}
ORDER BY is_synonym(u.status), u.id
