mapper.select:
SELECT 
FROM species_interaction si
WHERE si.dataset_key=#{datasetKey}
  AND si.type=#{type}::SPECIESINTERACTIONTYPE
  AND si.taxon_id = #{taxonId}
ORDER BY si.id

mapper.insert:
INSERT INTO species_interaction (dataset_key,taxon_id,related_taxon_id,id,, created, modified) SELECT #{newDatasetKey},coalesce(m1.id2,taxon_id),coalesce(m2.id2,related_taxon_id),nextval('species_interaction_${newDatasetKey}_id_seq'),, created, modified FROM species_interaction r
LEFT JOIN idmap_name_usage_${datasetKey} m1 ON m1.id=r.taxon_id LEFT JOIN idmap_name_usage_${datasetKey} m2 ON m2.id=r.related_taxon_id
WHERE dataset_key = #{datasetKey}

mapper.select:
SELECT 
FROM 
WHERE si.dataset_key=#{key.datasetKey} AND si.sector_key = #{key.id}
