mapper.select:
SELECT 
FROM 
WHERE tc.dataset_key = #{datasetKey}
  AND tc.type=#{type}::TAXONCONCEPTRELTYPE
  AND tc.taxon_id = #{taxonId}
ORDER BY tc.id

mapper.insert:
INSERT INTO taxon_concept_rel (dataset_key,taxon_id,related_taxon_id,id,, created, modified) SELECT #{newDatasetKey},coalesce(m1.id2,taxon_id),coalesce(m2.id2,related_taxon_id),nextval('taxon_concept_rel_${newDatasetKey}_id_seq'),, created, modified FROM taxon_concept_rel r
LEFT JOIN idmap_name_usage_${datasetKey} m1 ON m1.id=r.taxon_id LEFT JOIN idmap_name_usage_${datasetKey} m2 ON m2.id=r.related_taxon_id
WHERE dataset_key = #{datasetKey}

mapper.select:
SELECT 
FROM 
WHERE tc.dataset_key = #{key.datasetKey} AND tc.sector_key = #{key.id}
