mapper.select:
SELECT 
FROM 
WHERE p.dataset_key=#{key.datasetKey} AND p.id = #{key.id}

mapper.insert:
INSERT INTO taxon_property (dataset_key, taxon_id, id, , created, modified) SELECT #{newDatasetKey},coalesce(m.id2,taxon_id),nextval('taxon_property_${newDatasetKey}_id_seq'),, created, modified FROM taxon_property tp
LEFT JOIN idmap_name_usage_${datasetKey} m ON m.id=tp.taxon_id
WHERE dataset_key = #{datasetKey}

