mapper.insert:
INSERT INTO verbatim_source (id,dataset_key,) SELECT coalesce(m.id2,v.id),#{newDatasetKey}, FROM verbatim_source v
LEFT JOIN idmap_name_usage_${datasetKey} m ON m.id=v.id
WHERE dataset_key = #{datasetKey};
INSERT INTO verbatim_source_secondary (id,dataset_key, type,source_id,source_dataset_key) SELECT coalesce(m.id2,v.id),#{newDatasetKey}, type,source_id,source_dataset_key FROM verbatim_source_secondary v
LEFT JOIN idmap_name_usage_${datasetKey} m ON m.id=v.id
WHERE dataset_key = #{datasetKey}

