mapper.select:
SELECT key FROM dataset WHERE source_key = #{key} AND deleted IS NULL AND origin = #{origin}::DATASETORIGIN AND NOT private
AND key NOT IN #{k}
ORDER BY created DESC LIMIT 1

SELECT key FROM dataset
WHERE source_key = #{key} AND deleted IS NULL AND attempt = #{attempt}
LIMIT 1

SELECT key, gbif_key as gbifKey, title, alias FROM dataset
WHERE key = #{key}
LIMIT 1
